import React, { useContext, useEffect } from "react";
import PokemonContext from "../../context/pokemon/PokemonContext";
import japaneseNames from "../../japaneseNames";

export const PokemonInfo = () => {
  const pokemonContext = useContext(PokemonContext);
  const { getPokemon, pokemon, loading } = pokemonContext;

  useEffect(() => {
    getPokemon(11);
    if (pokemon) {
      console.log(pokemon);
    }
  }, []);

  return (
    <div classname="container">
      <div className="pokemonImageArea">
        <h5 className="pokemonImageArea__pokemonNumber"></h5>
        <h2 className="pokemonImageArea__pokemonName"></h2>
        <h1 className="pokemonImageArea__japaneseName"></h1>
      </div>
      <div className="pokemonStatsArea">
        <div className="pokemonStatsArea__icons"></div>
        <div className="pokemonStatsArea__stats"></div>
      </div>
    </div>
  );
};
