import React, { useContext, useEffect, Fragment } from "react";
import PokemonContext from "../../context/pokemon/PokemonContext";
import japaneseNames from "../../japaneseNames";
import ColorThief from "../../../node_modules/colorthief/dist/color-thief.mjs";

export const PokemonInfo = () => {
  const pokemonContext = useContext(PokemonContext);
  const { getPokemon, pokemon, loading } = pokemonContext;

  async function colorTime() {
    const colorThief = new ColorThief();
    const img = document.getElementById("pokemonimg");
    console.log(img);
    img.crossOrigin = "Anonymous";

    let color;

    if (img) {
      color = colorThief.getColor(img);
      console.log(color);
    }
  }

  useEffect(() => {
    getPokemon(11);
  }, []);

  return (
    <Fragment>
      {loading ? (
        <h1>Loading pokemon</h1>
      ) : (
        <div className="container">
          <div className="pokemonImageArea">
            <h5 className="pokemonImageArea__pokemonNumber">{pokemon.id}</h5>
            <h2 className="pokemonImageArea__pokemonName">{pokemon.name}</h2>
            <h1 className="pokemonImageArea__japaneseName">
              {japaneseNames[pokemon.id]}
            </h1>
            <img
              src={pokemon.sprite}
              alt="pokemon"
              id="pokemonimg"
              onload={colorTime()}
            />
          </div>
          <div className="pokemonStatsArea">
            <div className="pokemonStatsArea__icons">
              {/*  -- Needs to be a cap for the type */}
              {/* <img
                src={`https://upload.wikimedia.org/wikipedia/commons/3/3c/Pok%C3%A9mon_${pokemon.type}_Type_Icon.svg`}
                alt=""
              /> */}
              <p>{pokemon.type}</p>
            </div>

            <div className="pokemonStatsArea__stats">
              <p>HP: {pokemon.hp}</p>
              <p>ATTK: {pokemon.attack}</p>
              <p>DEF: {pokemon.defence}</p>
              <p>SP ATTK: {pokemon.special_attack}</p>
              <p>SP DEF: {pokemon.sspecial_defence}</p>
              <p>SPEED: {pokemon.speed}</p>
            </div>
          </div>
        </div>
      )}
    </Fragment>
  );
};
