import React, { Fragment, useState, useContext } from "react";

export const SearchBar = () => {
  const [searchText, setSearchtext] = useState("");

  useEffect(() => {
    console.log(searchText);
  }, [searchText]);

  return (
    <fragment>
      <input
        type="text"
        className="searchInput"
        onChange={(e) => {
          setSearchState(e.target.input);
        }}
      />
    </fragment>
  );
};
