import React, { useContext, useEffect, useState, useRef } from "react";
import io from "socket.io-client";
import ChatContext from "../../context/chat/ChatContext";

export const ChatWindow = () => {
  const chatContext = useContext(ChatContext);
  const { messages, onMessage } = chatContext;
  const [typing, setTyping] = useState([]);
  const [connceted, setConnected] = useState(false);
  const socketRef = useRef();

  useEffect(() => {
    console.log("Connecting to socket io");
    socketRef.current = io("http://localhost:3333");

    socketRef.current.on("connected", () => {
      setConnected(true);
    });

    socketRef.current.on("newMessage", (message) => {
      onMessage(message);
    });

    return () => socketRef.current.disconnect();
  }, [messages]);

  return (
    <div className="chatWindow">
      <div className="chatWindow__messages"></div>
      <div className="chatWindow__submitArea">
        <textarea className="chatWindow__submitArea__input" />
        <button className="chatWindow__submitArea__submitButton"></button>
      </div>
    </div>
  );
};
