import { NEW_POKEMON } from "../types";

export default (state, action) => {
  console.log("Payload, type and state below");
  console.log(action.payload);
  console.log(action.type);
  //
  switch (action.type) {
    case NEW_POKEMON:
      return {
        ...state,
        pokemon: action.payload,
      };
    default:
      return state;
  }
};
